			if (programKey[AppConstants.PROGRAM_KEY_MA] == 1) {
				maFlag = true;
			}
			if (programKey[AppConstants.PROGRAM_KEY_TANF] == 1) {
                tanfFlag = true;
            }
	        if (programKey[AppConstants.PROGRAM_KEY_SNAP] == 1) {
	            snapFlag = true;
	        }
